import React, {useState, useEffect} from "react";
import axios from "axios";

function App() {

  const api =  "https://cat-fact.herokuapp.com/facts"

  //state to hold the random fact 
  const [fact, setFact] = useState("");
  //state to hold the random number for fact 
  const [randomNumber, setRandomNumber] = useState("5");

  const keepTrack = false; 

  useEffect(() => {
    async function getData() {
      const [fetchedData, randomNumber] = await(fetchData());
      setRandomNumber(randomNumber);
      const randomFact = fetchedData[randomNumber].text
      setFact(randomFact)
    }
    getData();
    }, [keepTrack])

  async function fetchData() {
    try {
      const {data} = await axios.get(api);
      const numberOfFacts = data.all.length;
      const randomNumber = Math.floor(Math.random() * numberOfFacts) + 1  
      //setRandomNumber(randomNumber)
      //const randomFact = data.all[randomNumber].text
      return [data.all, randomNumber];
    } catch (error) {
      console.log(error)
    }
  }

  return(
    <h1>
      Hello World!
      {fact}
    </h1>
  )
};

export default App;
